#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#include <stdio.h>
#include <stdint.h>

int main(void)
{
	uint32_t* pClkCntrlReg = (uint32_t*) 0x40023830;
	uint32_t* pPortAModeReg = (uint32_t*) 0x40020000;
	uint32_t* pPortAInReg = (uint32_t*) 0x40020010;
	uint32_t* pPortAOutReg = (uint32_t*) 0x40020014;

	// enable clock for gpioa
	*pClkCntrlReg = *pClkCntrlReg | 0x00000000; // 0x00000001 to enable

	// configure the mode
	*pPortAModeReg = *pPortAModeReg &~ (3 << 2);
	*pPortAModeReg = *pPortAModeReg &~ (3 << 10);

	*pPortAModeReg = *pPortAModeReg | (1 << 10);

	uint8_t pinStatus;

	while(1) {
		pinStatus = (uint8_t) *pPortAInReg & (0x2);

		if(pinStatus) {
			*pPortAOutReg = *pPortAOutReg | (1 << 5);
		} else {
			*pPortAOutReg = *pPortAOutReg &~ (1 << 5);
		}
	}
}
